      let startRow = row - row % 3,
          startCol = col - col % 3;
          
      for(let i = 0; i < 3; i++)
          for(let j = 0; j < 3; j++)
              if (grid[i + startRow][j + startCol] == num)
                  return false;
  
      return true;
  }

